import React, { useEffect, useRef, useState } from 'react';
import cytoscape from 'cytoscape';
import './ArgumentDAG.css';

const ArgumentDAG = () => {
  const containerRef = useRef(null);
  const cyRef = useRef(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Check if container is available
        if (!containerRef.current) {
          console.error("Container ref not available");
          setError("Container reference not available. Please try refreshing the page.");
          setLoading(false);
          return;
        }

        console.log("Container ref available, fetching data...");

        const response = await fetch('http://localhost:8000/visualize-argument-dag', {
          method: 'GET',
          headers: {
            'X-API-Key': 'test_api_key_123',
            'Content-Type': 'application/json',
            'Accept': 'application/json'
          },
          mode: 'cors',
          credentials: 'same-origin'
        });

        if (!response.ok) {
          throw new Error(`API responded with status: ${response.status}`);
        }

        const data = await response.json();
        console.log("Data received:", data);

        if (!data || !data.nodes || !data.edges || data.nodes.length === 0) {
          setError("No graph data available. The API returned empty data.");
          setLoading(false);
          return;
        }

        // Format elements for Cytoscape
        const elements = {
          nodes: data.nodes.map(node => ({
            data: {
              id: node.id,
              label: node.label || node.text,
              type: node.type,
              details: node.details || ''
            }
          })),
          edges: data.edges.map(edge => ({
            data: {
              id: `edge-${edge.source}-${edge.target}`,
              source: edge.source,
              target: edge.target,
              label: edge.type,
              relationship: edge.type
            }
          }))
        };

        // Initialize Cytoscape
        cyRef.current = cytoscape({
          container: containerRef.current,
          elements: elements,
          style: [
            {
              selector: 'node',
              style: {
                'background-color': '#FF4136',
                'label': 'data(label)',
                'text-valign': 'center',
                'color': '#fff',
                'font-size': '12px',
                'width': 100,
                'height': 100,
                'text-wrap': 'wrap'
              }
            },
            {
              selector: 'edge',
              style: {
                'width': 2,
                'line-color': '#ccc',
                'target-arrow-shape': 'triangle',
                'target-arrow-color': '#ccc',
                'curve-style': 'bezier',
                'label': 'data(label)'
              }
            }
          ],
          layout: data.layout || {
            name: 'cose',
            animate: true,
            nodeDimensionsIncludeLabels: true
          }
        });

        // Add event listeners for filtering
        const filterByClaim = (claimType) => {
          cyRef.current.nodes().forEach(node => {
            if (node.data('type') === claimType) {
              node.style('opacity', 1);
            } else {
              node.style('opacity', 0.3);
            }
          });
        };

        const resetFilter = () => {
          cyRef.current.nodes().style('opacity', 1);
        };

        // Add event listeners to buttons if they exist
        const claimFilterBtn = document.getElementById('filter-claims');
        const resetFilterBtn = document.getElementById('reset-filter');

        if (claimFilterBtn) {
          claimFilterBtn.addEventListener('click', () => filterByClaim('claim'));
        }

        if (resetFilterBtn) {
          resetFilterBtn.addEventListener('click', resetFilter);
        }

        setLoading(false);
      } catch (err) {
        console.error("Error loading visualization:", err);
        setError(`Error loading visualization: ${err.message}`);
        setLoading(false);
      }
    };

    fetchData();

    // Cleanup function
    return () => {
      if (cyRef.current) {
        cyRef.current.destroy();
      }

      // Remove event listeners
      const claimFilterBtn = document.getElementById('filter-claims');
      const resetFilterBtn = document.getElementById('reset-filter');

      if (claimFilterBtn) {
        claimFilterBtn.removeEventListener('click', () => {});
      }

      if (resetFilterBtn) {
        resetFilterBtn.removeEventListener('click', () => {});
      }
    };
  }, []);

  return (
    <div className="dag-container">
      {loading && <div className="loading-message">Initializing visualization...</div>}
      {error && <div className="error-message">{error}</div>}
      <div 
        ref={containerRef} 
        className="cytoscape-container" 
        style={{ 
          width: '100%', 
          height: '600px',
          display: loading || error ? 'none' : 'block'
        }}
      />
    </div>
  );
};

export default ArgumentDAG;
