import React, { useEffect, useRef, useState } from 'react';
import cytoscape from 'cytoscape';
import tippy from 'tippy.js';
import 'tippy.js/dist/tippy.css';
import './ArgumentDAG.css'; // If you want separate styling

const ArgumentDAG = () => {
  const cyRef = useRef(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    console.log('Initializing visualization...');

    // Make sure the container is available
    const container = document.getElementById('cy');
    if (!container) {
      console.error('Container ref not available');
      setError('Container element not found');
      setLoading(false);
      return;
    }

    console.log('Container found, fetching data...');

    // Replace with your local or deployed API address
    fetch('http://localhost:8000/visualize-argument-dag', {
      method: 'GET',
      headers: {
        'X-API-Key': 'test_api_key_123',
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      mode: 'cors',
      cache: 'no-cache',
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`API responded with status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        console.log('Data received:', data);
        setLoading(false);

        if (!data.nodes || !data.edges) {
          console.error('Invalid data format received:', data);
          setError('Invalid data format received from API');
          return;
        }

        if (data.nodes.length === 0) {
          console.warn('No nodes found in the data');
          setError('No graph data available to display');
          return;
        }

        // Initialize Cytoscape
        const cy = cytoscape({
          container: container,
          elements: [
            ...data.nodes.map(node => ({
              data: {
                id: node.id,
                label: node.text || node.label,
                type: node.type || node.label,
                details: node.details || ''
              }
            })),
            ...data.edges.map(edge => ({
              data: {
                source: edge.source,
                target: edge.target,
                label: edge.type,
                relationship: edge.type
              }
            }))
          ],
          style: [
            {
              selector: 'node',
              style: {
                'background-color': '#0074D9',
                'label': 'data(label)',
                'text-valign': 'center',
                'color': '#fff',
                'font-size': '12px',
                'border-width': 2,
                'border-color': '#fff',
                'width': 100,
                'height': 100,
                'text-wrap': 'wrap',
                'text-max-width': '80px'
              }
            },
            {
              selector: '[type = "Claim"]',
              style: {
                'background-color': '#FF4136'
              }
            },
            {
              selector: '[type = "claim"]',
              style: {
                'background-color': '#FF4136'
              }
            },
            {
              selector: '[type = "Warrant"]',
              style: {
                'background-color': '#2ECC40'
              }
            },
            {
              selector: '[type = "Rebuttal"]',
              style: {
                'background-color': '#FF851B'
              }
            },
            {
              selector: 'edge',
              style: {
                'width': 2,
                'line-color': '#ccc',
                'target-arrow-shape': 'triangle',
                'target-arrow-color': '#ccc',
                'curve-style': 'bezier',
                'label': 'data(relationship)'
              }
            },
            {
              selector: '.highlighted',
              style: {
                'line-color': '#FFD700',
                'target-arrow-color': '#FFD700',
                'width': 4
              }
            }
          ],
          layout: data.layout || {
            name: 'cose',
            animate: true,
            nodeDimensionsIncludeLabels: true,
            refresh: 20,
            fit: true,
            padding: 30,
            randomize: false,
            componentSpacing: 100,
            nodeRepulsion: 400000,
            nodeOverlap: 10,
            idealEdgeLength: 100,
            edgeElasticity: 100,
            nestingFactor: 5,
            gravity: 80,
            numIter: 1000,
            initialTemp: 200,
            coolingFactor: 0.95,
            minTemp: 1.0
          }
        });

        cyRef.current = cy;

        console.log('Cytoscape initialized with elements:', cy.elements().length);

        // Node tooltips (Tippy.js)
        cy.nodes().forEach(node => {
          const ref = node.popperRef();
          tippy(ref, {
            content: `Type: ${node.data('type')}\nDetails: ${node.data('details') || 'N/A'}`,
            trigger: 'mouseenter',
            placement: 'top',
            arrow: true,
            theme: 'light',
          });
        });

        // Edge hover highlight
        cy.on('mouseover', 'edge', evt => {
          evt.target.addClass('highlighted');
        });
        cy.on('mouseout', 'edge', evt => {
          evt.target.removeClass('highlighted');
        });

        // Clickable edge interactions
        cy.on('tap', 'edge', evt => {
          const edge = evt.target;
          alert(`Relationship: ${edge.data('relationship')}\nSource: ${edge.data('source')}\nTarget: ${edge.data('target')}`);
        });

        // Run layout
        cy.layout(data.layout || { name: 'cose' }).run();
      })
      .catch(error => {
        console.error('Error fetching or processing DAG:', error);
        setError(`Error loading visualization: ${error.message}`);
        setLoading(false);
      });

    // Cleanup function
    return () => {
      if (cyRef.current) {
        cyRef.current.destroy();
      }
    };
  }, []);

  // Add event handlers for the filter buttons
  useEffect(() => {
    const filterClaimsBtn = document.getElementById('filter-claims');
    const resetFilterBtn = document.getElementById('reset-filter');

    if (filterClaimsBtn && resetFilterBtn) {
      const handleFilterClaims = () => {
        if (cyRef.current) {
          cyRef.current.nodes().forEach(node => {
            const type = node.data('type').toLowerCase();
            node.style('display', type === 'claim' ? 'element' : 'none');
          });
        }
      };

      const handleResetFilter = () => {
        if (cyRef.current) {
          cyRef.current.nodes().forEach(node => {
            node.style('display', 'element');
          });
        }
      };

      filterClaimsBtn.addEventListener('click', handleFilterClaims);
      resetFilterBtn.addEventListener('click', handleResetFilter);

      return () => {
        filterClaimsBtn.removeEventListener('click', handleFilterClaims);
        resetFilterBtn.removeEventListener('click', handleResetFilter);
      };
    }
  }, []);

  return (
    <div className="dag-container">
      <div id="cy" style={{ width: '100%', height: '600px', border: '1px solid #ccc' }}>
        {loading && <div className="loading-message">Loading visualization...</div>}
        {error && <div className="error-message">{error}</div>}
      </div>
      <div className="controls">
        <button id="filter-claims">Show Only Claims</button>
        <button id="reset-filter">Reset Filter</button>
      </div>
    </div>
  );
};

export default ArgumentDAG;
